mapper.select:
SELECT cont, REVIEW_NUMBER, USER_NUMBER, PROJECT_NUMBER, CONTENT, REGISTER_DATE FROM( SELECT cont, ROWNUM, CATEGORY, REVIEW_NUMBER, USER_NUMBER, PROJECT_NUMBER, CONTENT, REGISTER_DATE FROM( SELECT COUNT(li.USER_NUMBER) cont, tp.CATEGORY, re.REVIEW_NUMBER, re.USER_NUMBER, re.PROJECT_NUMBER, re.CONTENT, re.REGISTER_DATE FROM TBL_REVIEW re JOIN TBL_LIKE li ON re.REVIEW_NUMBER = li.REVIEW_NUMBER JOIN TBL_PROJECT TP ON re.PROJECT_NUMBER = tp.PROJECT_NUMBER
WHERE pr.CATEGORY = #{category}
WHERE re.PROJECT_NUMBER = #{projectNumber}
GROUP BY tp.PROJECT_NUMBER, tp.CATEGORY, re.REVIEW_NUMBER, re.USER_NUMBER, re.PROJECT_NUMBER, re.CONTENT, re.REGISTER_DATE ) WHERE ROWNUM <= #{criteria.pageNum} * #{criteria.amount}
ORDER BY cont DESC
ORDER BY ROWNUM DESC
) WHERE ROWNUM > (#{criteria.pageNum} - 1) * #{criteria.amount}

SELECT NICKNAME, CONTENT, REGISTER_DATE
FROM TBL_REVIEW
WHERE REVIEW_NUMBER = #{reviewNumber}

mapper.update:
UPDATE TBL_REVIEW
SET CONTENT = #{content}, REGISTER_DATE = SYSDATE
WHERE REVIEW_NUMBER = #{reviewNumber}

mapper.select:
SELECT TOTAL,
REVIEW_NUMBER, USER_NUMBER, PROJECT_NUMBER, CONTENT, REGISTER_DATE,
CATEGORY, STATUS,
EMAIL
FROM(
    SELECT ROWNUM RN, TOTAL, REVIEW_NUMBER, USER_NUMBER, PROJECT_NUMBER, CONTENT, REGISTER_DATE,
    CATEGORY, STATUS,
    EMAIL
    FROM(
        SELECT COUNT(r.REVIEW_NUMBER) OVER() AS TOTAL,
        r.REVIEW_NUMBER, r.USER_NUMBER, r.PROJECT_NUMBER, r.CONTENT, r.REGISTER_DATE,
        p.CATEGORY, p.STATUS,
        u.EMAIL
        FROM TBL_USER u
        INNER JOIN TBL_REVIEW r
        ON r.USER_NUMBER = u.USER_NUMBER
        INNER JOIN TBL_PROJECT p
        ON p.PROJECT_NUMBER = r.PROJECT_NUMBER
        WHERE


                u.${type}


                r.${type}


        LIKE '%'||#{keyword}||'%'

            AND p.CATEGORY = #{category}



        AND TO_CHAR(r.REGISTER_DATE, 'yyyy-mm-dd') >= #{startDate}




        AND TO_CHAR(r.REGISTER_DATE, 'yyyy-mm-dd') <= #{endDate}



            AND p.STATUS = #{status}

    )

        WHERE ROWNUM <= #{pageNum} * #{amount}

)

WHERE RN > (#{pageNum} - 1) * #{amount}

SELECT COUNT(REVIEW_NUMBER)
FROM TBL_REVIEW
WHERE TO_CHAR(REGISTER_DATE, 'yyyy-mm-dd') = #{date}
